#Part 1
def count(inp):
    res = 0
    for ch in inp:
        if ch == "(":
            res += 1
        else:
            res -= 1
    return res

print(count(inp1))
print(count(inp2))


# Part 2
def get_pos(inp):
    res = 0
    for i, ch in enumerate(inp):
        if ch == "(":
            res += 1
        else:
            res -= 1
        if res == -1:
            return i + 1

print(get_pos(inp1))
print(get_pos(inp2))